import requestsimport PyPDF2 url = 'enter-url-to-pdf-here'r = requests.get(url, allow_redirects=True)open('text.pdf', 'wb').write(r.content)pdfFileObj = open('text.pdf', 'rb') pdfReader = PyPDF2.PdfFileReader(pdfFileObj)  i=0print(pdfReader.numPages)pages = pdfReader.getNumPages()print(pages)content = ''for i in range(pages):    pageObj = pdfReader.getPage(i)     print(pageObj.extractText())     content += (' ' + pageObj.extractText())  with open("text.txt", "w") as text_file:    text_file.write(content)pdfFileObj.close() 